		var con = this.contacts[i];
		con.r1 = vsub(con.p, a.p);
		con.r2 = vsub(con.p, b.p);
		con.nMass = 1/k_scalar(a, b, con.r1, con.r2, con.n);
		con.tMass = 1/k_scalar(a, b, con.r1, con.r2, vperp(con.n));
		con.bias = -bias*min(0, con.dist + slop)/dt;
		con.jBias = 0;
